INSERT INTO APP_TEMAS(DESCRIPCION, LIN_ID, USU_CREO, FEC_CREO) VALUES('Programas de lectura, escritura y oralidad con especial énfasis en la participación de familias, niños, jóvenes y grupos intergeneracionales, como: tertulias literarias; clubes de lectura (literarios, de divulgación científica, sobre temas de actualidad o informativos, entre otros, con material en formato impreso o digital); programas dirigidos a personas con discapacidad; programas en medios digitales y de comunicación públicos y/o comunitarios; programas en espacios no convencionales; proyectos de circulación de contenidos digitales; proyectos de promoción de la oralidad y/o gestión de contenidos orales y talleres de escritura creativa de la red Relata*.', '1', 'ADMIN', GETDATE())
INSERT INTO APP_TEMAS(DESCRIPCION, LIN_ID, USU_CREO, FEC_CREO) VALUES('Programas de lectura, escritura y oralidad en veredas y corregimientos que son parte del área de influencia de la biblioteca, como círculos de la palabra, clubes de lectura, bibliotecas humanas, producción de contenidos locales y circulación de memorias e historias locales, registro de prácticas ancestrales (en torno a saberes y conocimientos propios, literarios, de divulgación científica, de temas de interés y actualidad, entre otros, con material en formato impreso, digital u oral). Con énfasis en la articulación de estos proyectos con instituciones educativas, comunitarias, sociales y culturales de la comunidad. Estos proyectos deben integrar la circulación de las colecciones impresas, audiovisuales, digitales y con lenguajes diferenciales (población con discapacidad) de la biblioteca.', '1', 'ADMIN', GETDATE())
