--LINEAS
INSERT INTO APP_LINEAS (VIG_ID, LIN_NOMBRE, LIN_DESCRIPCION, USU_CREO, FEC_CREO) VALUES(1, 'L1.1 - Lectura, escritura y oralidad "Leer es mi cuento": Bibliotecas públicas que forman parte de la Red Nacional de Bibliotecas Públicas coordinadas por la Biblioteca Nacional de Colombia.', 'Proyectos presentados por bibliotecas públicas que forman parte de la Red Nacional de Bibliotecas Públicas coordinadas por la Biblioteca Nacional de Colombia','ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L1.2 - Lectura, escritura y oralidad "Leer es mi cuento": Bibliotecas públicas, comunitarias y estrategias bibliotecarias itinerantes en espacios no convencionales que NO forman parte de la Red Nacional de Bibliotecas Públicas:', 'Proyectos presentados por bibliotecas públicas, comunitarias y estrategias bibliotecarias itinerantes en espacios no convencionales que NO forman parte de la Red Nacional de Bibliotecas Públicas:',  'ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L2.1 - Actividades artísticas y culturales de duración limitada: Carnavales, ferias y fiestas', 'Carnavales, ferias y fiestas, como por ejemplo festivales de las artes, de las letras, muestras y festivales de cine y audiovisuales, carnavales, ferias y fiestas tradicionales, festivales de artes y medios digitales, festivales de nuevos medios y medios interactivos e inmersivos (tecnología que intenta emular un mundo físico a través de un mundo digital o simulado).',  'ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L2.2 - Actividades artísticas y culturales de duración limitada: Conciertos o eventos artísticos y culturales', 'Conciertos o eventos artísticos y culturales como por ejemplo recitales, conciertos, eventos artísticos corales, encuentros culturales o académicos de saberes artísticos y culturales, eventos artísticos y culturales en conexión con la cultura digital.',  'ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L3 - Fortalecimiento y circulación de procesos artísticos, culturales y de la economía naranja con una duración mínima de seis (6) meses', 'Comprende proyectos de creación, circulación, apropiación de contenidos, desarrollo de audiencias, difusión e investigación cultural y de la economía creativa, estudios que soporten y respalden la formulación e implementación de políticas culturales y de la economía naranja, la programación artística y la salvaguardia del patrimonio material e inmaterial, que se desarrollan de manera continuada, con una duración de mínima seis (6) meses.',  'ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L4 - Programas presenciales, semipresenciales y/o virtuales de formación artística, cultural y de la economía naranja, con una duración mínima de cuatro (4) meses: ', 'Comprende proyectos encaminados a la formación en los diversos campos de la cultura, las artes, el patrimonio cultural y la economía naranja a través de programas de educación informal con una duración mínima de cuatro (4) meses, dirigidos a formadores o multiplicadores y población en general en los diferentes procesos de áreas ',  'ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L5.1 - Investigación y fortalecimiento organizacional para las artes, el patrimonio cultural y la economía naranja', 'Comprende proyectos productivos y/o comunitarios culturales o de las industrias culturales y creativas, dirigidos a la creación y producción de bienes y servicios culturales, al fortalecimiento organizacional de los mismos, a la generación de redes colaborativas y asociatividad relacionados con los siguientes sectores: Artes y patrimonio (artes visuales, artes escénicas, turismo cultural, patrimonio cultural inmaterial), Industrias culturales (medios digitales, videojuegos, animación, medios interactivos e inmersivos,audiovisuales, fonograbado, editorial) y creaciones funcionales (desarrollos TIC, software de contenidos, diseño). ',  'ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L5.2 - Investigación y fortalecimiento organizacional para las artes, el patrimonio cultural y la economía naranja:', 'Comprende proyectos de innovación en la circulación, distribución, comercialización y acceso a bienes y servicios culturales y creativos asociados a los sectores: Artes y patrimonio (artes visuales, artes escénicas, turismo cultural, patrimonio cultural inmaterial), Industrias culturales (medios digitales, videojuegos, animación, medios interactivos e inmersivos,audiovisuales, fonograbado, editorial) y creaciones funcionales (desarrollos TIC, software de contenidos, diseño). ',  'ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L6 - Circulación artística a escala nacional', 'Comprende proyectos que fomenten, promocionen y difundan las expresiones artísticas y las manifestaciones del patrimonio cultural mediante la circulación y la participación, presencial y/o virtual, de los artistas nacionales en el territorio colombiano en presentaciones temporadas o giras por invitación o por iniciativa de la entidad que presenta el proyecto, y en intercambios, presentación de grupos de proyección, circuitos concertados de festivales, carnavales o fiestas tradicionales que se desarrollen en diversas ciudades del país, que se desarrollen en/desde diferentes escenarios utilizando medios de comunicación comunitarios, regionales, públicos, universitarios, redes sociales u otros medios de divulgación digital a través de la implementación de estrategias virtuales y la incorporación de metodologías tecnológicas que permitan acceder a la oferta artística o a los producciones de obras en medios alternativos.',  'ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L7 - Fortalecimiento cultural a contextos poblacionales específicos', 'Comprende proyectos para la recuperación y el fortalecimiento de prácticas culturales de los pueblos indígenas, comunidades negras, afrocolombianas, raizales,  palenqueras, Pueblo Gitano o Rrom',  'ADMIN', GETDATE())
INSERT INTO APP_LINEAS (VIG_ID,LIN_NOMBRE,LIN_DESCRIPCION, USU_CREO, FEC_CREO ) VALUES('1', 'L8 - Prácticas culturales de la población con discapacidad', 'Comprende proyectos artísticos y culturales encaminados a fomentar el reconocimiento, la inclusión socio cultural y la participación de las personas con discapacidad, orientados a estimular la gestión, la formación, la creación, la investigación y la circulación, que permitan hacer visible la participación de la comunidad y en especial de niños, niñas, adolescentes, jóvenes, personas adultas mayores, comunidades Negras, Afrocolombianas, Raizales y Palenqueras, Pueblos Indígenas , Pueblo Rrom, entre otros.',  'ADMIN', GETDATE())

